import { Component, OnInit, ViewChild } from '@angular/core';
import { Employee } from '../employee';
import { EmployeeService } from '../employee.service';
import { Router } from '@angular/router';
import { Page } from 'src/app/interface/page';
import { ApiResponse } from '../../interface/ApiResponse';
import { Observable, map, of, startWith } from 'rxjs';
import { HttpErrorResponse } from '@angular/common/http';
import { catchError } from 'rxjs/operators';


@Component({
  selector: 'app-employee-list',
  templateUrl: './employee-list.component.html',
  styleUrls: ['./employee-list.component.css']
})
export class EmployeeListComponent implements OnInit{
  employees: Employee[] = [];
  page: Page = {} as Page;
  apiResponse: ApiResponse<Page> = {} as ApiResponse<Page>;

  constructor(private employeeService: EmployeeService,
    private router: Router){}

  employeesState$: Observable<{appState: string, appData?: ApiResponse<Page>, error?: HttpErrorResponse}>;

  ngOnInit(): void{
    // this.getEmployees();
    this.employeesState$ = this.employeeService.employees$().pipe(
      map((response: ApiResponse<Page>) => {
        console.log((response));
        return ({ appState: 'APP_LOAD', appData: response });
      }
      ),
      startWith({appState: 'APP_LOADING'}),
      catchError((error: HttpErrorResponse) => of({appState: 'APP_ERROR', error}))
    )
  }




  // private getEmployees(){
  //   this.employeeService.getEmployeesList(0,10).subscribe((data) => {
  //     console.log(data);
  //     this.apiResponse = data;
  //   });
  // }

  updateEmployee(id: number){
    this.router.navigate(['update-employee', id]);
  }

  detailEmployee(id: number){
    this.router.navigate(['detail-employee', id]);
  }

  deleteEmployee(id: number){
    this.employeeService.deleteEmployee(id).subscribe(data => {
      console.log(data)
      // this.getEmployees();
    })
  }
}
<h2>Customer List</h2>

<ng-container *ngIf="(employeesState$ | async) as state" [ngSwitch]="state.appState">

  <table class="table table-striped" border="1px">
    <thead>
      <tr>
        <th>FullName</th>
        <th>Address</th>
        <th>Email</th>
        <th>Phone Number</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let employee of employees">
          <td>{{employee.fullName}}</td>
          <td>{{employee.address}}</td>
          <td>{{employee.email}}</td>
          <td>{{employee.phoneNumber}}</td>
          <td>
            <button (click)="updateEmployee(employee.id)" class = "btn btn-info">Update</button>
            <button (click)="deleteEmployee(employee.id)" class = "btn btn-danger" style="margin-left: 10px">Delete</button>
            <button (click)="detailEmployee(employee.id)" class = "btn btn-info" style="margin-left: 10px">Account Bank</button>
          </td>
      </tr>
    </tbody>

  </table>
  <div>
    <th>
      <button class = "add-button">
        <a href="http://localhost:4200/create-employee" class = "add-customer">Add Customer</a>
      </button>
    </th>
    <th>
      <nav aria-label="Page navigation example">
        <ul class="pagination">
          <li class="page-item"><a class="page-link">Previous</a></li>

          <li *ngFor="let pageNumber of [].constructor(page.totalPages); let i = index;" class="page-item">
            <a class="page-link">
              {{i}}
            </a>
          </li>

          <li class="page-item"><a class="page-link">Next</a></li>
        </ul>
      </nav>
    </th>
  </div>
</ng-container>
