import { Component } from '@angular/core';
import { Employee } from '../employee';
import { ActivatedRoute } from '@angular/router';
import { EmployeeService } from '../employee.service';

@Component({
  selector: 'app-employee-details',
  templateUrl: './employee-details.component.html',
  styleUrls: ['./employee-details.component.css']
})
export class EmployeeDetailsComponent {
  id: any;
  employee: Employee = {} as Employee;
  constructor(private route: ActivatedRoute,
    private employeeService: EmployeeService){}

    ngOnInit(): void{
      this.id = this.route.snapshot.params['id'];
      this.employeeService.getEmployeeById(this.id).subscribe(data =>{
        this.employee = data;
      });
    }

}
<h3>View Account Employee Details</h3>
<div>
  <table>
    <div>
      <label><b>First Name: </b></label> {{employee.fullName}}
    </div>
    <div>
      <label><b>Last Name: </b></label> {{employee.address}}
    </div>
    <div>
      <label><b>Email Id: </b></label> {{employee.email}}
    </div>
    <div>
      <label><b>Phone Number: </b></label> {{employee.phoneNumber}}
    </div>
  </table>

</div>
