import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { EmployeeService } from 'src/app/Service/employee.service';
import { AccountBankService } from '../account-bank.service';
import { AccountBank } from 'src/app/interface/account-bank';
import { NgForm } from '@angular/forms';

@Component({
  selector: 'app-create-account-bank',
  templateUrl: './create-account-bank.component.html',
  styleUrls: ['./create-account-bank.component.css']
})
export class CreateAccountBankComponent {

  id: number;
  accountBank: AccountBank;

  constructor(private accountBankService: AccountBankService,
    private router: Router){}

    createAccount(createForm: any){
      this.accountBankService.createAccountBank(createForm.value, this.id).subscribe(data =>{
        console.log(createForm);
        alert("Create SuccessFully");
        this.router.navigate(['employees']);
        }
      );
    }
}
<div class = "col-md-8 offset-md-3">

  <h3>Create Account Bank</h3>

  <form #createForm="ngForm" (ngSubmit)="createAccount(createForm)" clas>
    <input type="text" ngModel name="bank" id="bank" placeholder="Enter your bank"
    class="form-control mb-3" required>
    <input type="text" ngModel name="accountNumber" id="accountNumber" placeholder="Enter your AccountNumber"
    class="form-control mb-3" required>
    <input type="text" ngModel name="branch" id="branch" placeholder="Enter your branch"
    class="form-control mb-3" required>
    <input type="text" ngModel name="cardType" id="cardType" placeholder="Enter your CardType"
    class="form-control mb-3" required>
    <input type="submit" value="Submit" class="">
  </form>
</div>
