import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Employee } from './interface/employee';
import { CreateEmployeeComponent } from './create-employee/create-employee.component';
import { ApiResponse } from './interface/api-response';
import { Page } from './interface/page';


@Injectable({
  providedIn: 'root'
})
export class EmployeeService {

  private baseURL = "http://localhost:8080/api/v1/employees";
  private baseURL_save = "http://localhost:8080/api/v1/create-employee";
  private baseURL_update = "http://localhost:8080/api/v1/update-employee";
  private baseURL_delete = "http://localhost:8080/api/v1/delete-employee";



  constructor(private httpClient: HttpClient) { }

  employees$ = (name: string = '', pageNo: number = 0, pageSize: number = 10): Observable<ApiResponse<Page>> =>
  this.httpClient.get<ApiResponse<Page>>(`${this.baseURL}?name=${name}&pageNo=${pageNo}&pageSize=${pageSize}`);

  getEmployeesList(name: string = '', pageNo: number = 0, pageSize: number = 10): Observable<ApiResponse<Page>>{
    return this.httpClient.get<ApiResponse<Page>>(`${this.baseURL}?name=${name}&pageNo=${pageNo}&pageSize=${pageSize}`);
  }

  // getEmployeesList(): Observable<Employee[]>{
  //   return this.httpClient.get<Employee[]>(`${this.baseURL}`);
  // }

  createEmployee(employee: Employee): Observable<Object>{
    return this.httpClient.post(`${this.baseURL_save}`, employee);
  }

  getEmployeeById(id: number): Observable<Employee>{
    return this.httpClient.get<Employee>(`${this.baseURL}/${id}`);
  }

  updateEmployee(id: number, employee: Employee): Observable<Object>{
    return this.httpClient.put(`${this.baseURL}/update-employee/${id}`, employee);
  }
  detailEmployee(id: number){
    return this.httpClient.get(`${this.baseURL}/${id}`);
  }
  deleteEmployee(id: number): Observable<Object>{
    return this.httpClient.delete(`${this.baseURL_delete}/${id}`);
  }
  // login(loginData){

  // }
}
