import { Component } from '@angular/core';
import { EmployeeService } from './Service/employee.service';
import { Router } from '@angular/router';
import { UserAuthService } from './Service/user-auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Employee_management';

  constructor(
    public employeeService: EmployeeService,
    private router: Router,
    private userAuthService: UserAuthService){}

  isLoggedIn(){
    return this.userAuthService.isLoggedIn();
  }
  logout(){
    this.userAuthService.clear();
    alert("Bạn đã đăng xuất thành công");
    this.router.navigate(['employees']);
  }
  addFunction(){
    if(this.userAuthService.getRoles() === 'admin'){
      this.router.navigate(['/create-employee']);
    }
    else{
      alert("you need to contact the admin to do that!");
    }
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Document</title>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
</head>
<body>
  <div id="header">
      <div class="socialtitle social">
        <a href="https://www.facebook.com/Hashtech-Academy-104675144484652" target="_blank"><i class="fa-brands fa-facebook"></i></a>
        <a href="https://www.instagram.com/hashtechacademy/" target="_blank"><i class="fa-brands fa-instagram"></i></a>
        <a href="https://twitter.com/HashtechA" target="_blank"><i class="fa-regular fa-envelope"></i></a>
        <a href="https://www.youtube.com/channel/UCcGQTLRIjg4A-Dc6LeLH6KA?view_as=subscriber" target="_blank"><i class="fa-brands fa-youtube"></i></a>
      </div>
    <div>
      <ul class = "nav">
        <li class="link-header">
          <a routerLink="employees" routerLinkActive="active" class="nav-link">Home</a>
        </li>
        <li class="link-header">
          <a routerLinkActive="active" class="nav-link" (click)="addFunction()">Add Employee</a>
        </li>
        <!-- <li class="btn-header">
          <button *ngIf="! isLoggedIn()" type="button" class="btn btn-success button-login x-4" routerLink="/login">Login</button>
          <button *ngIf="isLoggedIn()" type="button" class="btn btn-danger button-logout" (click)="logout()" >Logout</button>
        </li> -->
      </ul>
      <!-- *ngIf="isLoggedIn()"
      *ngIf="employeeService.roleMatch(['user'])" -->
    </div>

  </div>

  <section>
    <div class="container">
      <router-outlet></router-outlet>
    </div>
  </section>
</body>
</html>



