import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { EmployeeService } from 'src/app/Service/employee.service';
import { AccountBankService } from '../account-bank.service';
import { AccountBank } from 'src/app/interface/account-bank';

@Component({
  selector: 'app-create-account-bank',
  templateUrl: './create-account-bank.component.html',
  styleUrls: ['./create-account-bank.component.css']
})
export class CreateAccountBankComponent {

  id: number;
  accountBank: AccountBank;

  constructor(private accountBankService: AccountBankService,
    private router: Router){}

    saveAccountBank(){
      this.accountBankService.createAccountBank(this.accountBank, this.id).subscribe(data => {
        console.log(data);
        this.goToAccountBank();
      },
      error => console.log(error));
    }

    //Điều hướng đến trang employees khi submit
    goToAccountBank(){
      this.router.navigate(['/create-account-bank']);
    }

    onSubmit(){
      console.log(this.accountBank);
      alert("Create SuccessFully");
      this.saveAccountBank();
    }
}
<div class = "col-md-8 offset-md-3">

  <h3>Create Account Bank</h3>

  <form (ngSubmit) = "onSubmit()">
    <div class="form-group">
      <label>Bank</label>
      <input type="text" class = "form-control" id = "username"
        [(ngModel)] = "accountBank.bank" name = "username">
    </div>

    <div class="form-group">
      <label>AccountNumber</label>
      <input type="text" class = "form-control" id = "address"
        [(ngModel)] = "accountBank.accountNumber" name = "address">
    </div>

    <div class="form-group">
      <label>Branch</label>
      <input type="text" class = "form-control" id = "email"
        [(ngModel)] = "accountBank.branch" name = "email">
    </div>

    <div class="form-group">
      <label>CardType</label>
      <input type="text" class = "form-control position" id = "phoneNumber"
        [(ngModel)] = "accountBank.cardType" name = "phoneNumber">
    </div>
    <button class = "btn btn-success" type="submit">Submit</button>
  </form>
</div>
