import { Component } from '@angular/core';
import { Employee } from '../interface/employee';
import { EmployeeService } from '../Service/employee.service';
import { Router } from '@angular/router';
import { UserAuthService } from '../Service/user-auth.service';

@Component({
  selector: 'app-register-account',
  templateUrl: './register-account.component.html',
  styleUrls: ['./register-account.component.css']
})
export class RegisterAccountComponent {
  employees: Employee[] = [];
  constructor(private employeeService: EmployeeService,
    private router: Router,
    private userAuthService: UserAuthService){}

  registerFunction(signUpForm: any){
    this.employeeService.signup(signUpForm.value).subscribe(
      (response: any)=>{
        console.log(response);
        console.log(response.role);
        this.userAuthService.setRoles(response.role);
        this.router.navigate(['/employees']);
        const role = response.role;
      },
      (Error) => {
        alert("Tài khoản đã tồn tại");
      }
    );
  }
}
<form #signUpForm="ngForm" (ngSubmit)="registerFunction(signUpForm)" clas>
  <input type="text" ngModel name="email" id="email" placeholder="Enter your email"
  class="form-control mb-3">
  <input type="password" ngModel name="password" id="password" placeholder="Enter your password"
  class="form-control mb-3">
  <input type="submit" value="Login"
  class="">
</form>
