import { Component } from '@angular/core';
import { EmployeeService } from './employee/employee.service';
import { BehaviorSubject, Observable, catchError, map, of, startWith } from 'rxjs';
import { Router } from '@angular/router';
import { ApiResponse } from './interface/ApiResponse';
import { Page } from './interface/page';
import { Employee } from './employee/employee';
import { HttpErrorResponse } from '@angular/common/http';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'Customer_management';
  employees: Employee[] = [];
  page: Page = {} as Page;
  apiResponse: ApiResponse<Page> = {} as ApiResponse<Page>;
  responseSubject = new BehaviorSubject<ApiResponse<Page>>(null);

  private currentPageSubject = new BehaviorSubject<number>(0);;
  currentPage$ = this.currentPageSubject.asObservable();

  constructor(private employeeService: EmployeeService,
    private router: Router){}

  employeesState$: Observable<{appState: string, appData?: ApiResponse<Page>, error?: HttpErrorResponse}>;

  ngOnInit(): void{
    // this.getEmployees();
    this.employeesState$ = this.employeeService.employees$().pipe(
      map((response: ApiResponse<Page>) => {

        //giữ 1 copy response
        this.responseSubject.next(response);

        this.currentPageSubject.next(response.data.page.number)
        console.log((response));
        return ({ appState: 'APP_LOAD', appData: response });
      }
      ),
      startWith({appState: 'APP_LOADING'}),
      catchError((error: HttpErrorResponse) => of({appState: 'APP_ERROR', error}))
    )
  }
  gotToPage(pageNumber?: number): void{
    this.employeesState$ = this.employeeService.employees$(pageNumber).pipe(
      map((response: ApiResponse<Page>) => {
        this.responseSubject.next(response);
        this.currentPageSubject.next(pageNumber)

        console.log((response));
        return ({ appState: 'APP_LOADED', appData: response });
      }
      ),
      startWith({appState: 'APP_LOADED', appData: this.responseSubject.value}),
      catchError((error: HttpErrorResponse) => of({appState: 'APP_ERROR', error}))
    )
  }
  goToNextOrPreviousPage(direction?: string): void{
    this.gotToPage(direction === 'forward' ? this.currentPageSubject.value + 1 : this.currentPageSubject.value - 1);
  }



  // private getEmployees(){
  //   this.employeeService.getEmployeesList(0,10).subscribe((data) => {
  //     console.log(data);
  //     this.apiResponse = data;
  //   });
  // }

  updateEmployee(id: number){
    this.router.navigate(['update-employee', id]);
  }

  detailEmployee(id: number){
    this.router.navigate(['detail-employee', id]);
  }

  deleteEmployee(id: number){
    this.employeeService.deleteEmployee(id).subscribe(data => {
      console.log(data)
      // this.getEmployees();
    })
  }
}

  <nav class="navbar navbar-expand-sm bg-primary navbar-dark">
    <ul class = "navbar-nav">
      <li class = "nav-item">
        <a routerLink="employees" routerLinkActive="active" class="nav-link">Customer List</a>
      </li>
      <li class = "nav-item">
        <a routerLink="account-banks" routerLinkActive="active" class="nav-link">Account bank</a>
      </li>
    </ul>
  </nav>
  <h1 class = "text_center">{{title}}</h1>
  <div class="container">
    <h2>Customer List</h2>

<ng-container *ngIf="(employeesState$ | async) as state" [ngSwitch]="state.appState">
<div class="container">
  <ng-container *ngSwitchCase="'APP_ERROR'">{{state.error | json}}</ng-container>
  <ng-container *ngSwitchCase="'APP_LOAING'">
    <div class="d-flex justify-content-center mt-4">
      <div class="spinner-border" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    </div>
  </ng-container>

  <ng-container *ngSwitchCase="'APP_LOAD'">
    <table class="table table-striped" border="1px">
    <thead>
      <tr>
        <th>FullName</th>
        <th>Address</th>
        <th>Email</th>
        <th>Phone Number</th>
        <th>Actions</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let employee of state.appData.data.page.content; let i = index">
          <td>{{employee.fullName}}</td>
          <td>{{employee.address}}</td>
          <td>{{employee.email}}</td>
          <td>{{employee.phoneNumber}}</td>
          <td>
            <button (click)="updateEmployee(employee.id)" class = "btn btn-info">Update</button>
            <button (click)="deleteEmployee(employee.id)" class = "btn btn-danger" style="margin-left: 10px">Delete</button>
            <button (click)="detailEmployee(employee.id)" class = "btn btn-info" style="margin-left: 10px">Account Bank</button>
          </td>
      </tr>
    </tbody>
  </table>
  </ng-container>

</div>


  <div>
    <th>
      <button class = "add-button">
        <a href="http://localhost:4200/create-employee" class = "add-customer">Add Customer</a>
      </button>
    </th>
    <th>
      <nav aria-label="Page navigation example">
        <ul class="pagination">
          <li class="page-item">
            <a (click) = "goToNextOrPreviousPage('backward')" class="page-link">Previous</a>
          </li>

          <li *ngFor="let pageNumber of [].constructor(state.appData.data.page.totalPages); let i = index;" class="page-item">
            <a (click)="gotToPage(i)" class="page-link">
              {{i+1}}
            </a>
          </li>

          <li class="page-item">
            <a (click) = "goToNextOrPreviousPage('forward')" class="page-link">Next</a>
          </li>
        </ul>
      </nav>
    </th>
  </div>
</ng-container>

  </div>
  <footer class="footer">
    <div class="container">
      <span>This web was built by @Hivuong</span>
    </div>
  </footer>
