import { Component, OnInit } from '@angular/core';
import { Employee } from '../interface/employee';
import { EmployeeService } from '../Service/employee.service';
import { Router } from '@angular/router';
import { UserAuthService } from '../Service/user-auth.service';
import { NgForm } from '@angular/forms';
import { AccountBank } from '../interface/account-bank';

@Component({
  selector: 'app-register-account',
  templateUrl: './register-account.component.html',
  styleUrls: ['./register-account.component.css']
})
export class RegisterAccountComponent implements OnInit{
  accountBank: AccountBank;
  constructor(private employeeService: EmployeeService,
    private router: Router,
    private userAuthService: UserAuthService){}

  ngOnInit(): void {
    throw new Error('Method not implemented.');
  }
  saveAccount(){
    this.employeeService.createEmployee(this.accountBank).subscribe(data => {
      console.log(data);
      this.router.navigate(['/login']);
    },
    error => console.log(error));
  }
  onSubmit(){
    console.log(this.accountBank);
    alert("Register SuccessFully");
    this.saveAccount();
  }
  signUp(signUpForm: NgForm){
    this.employeeService.signup(signUpForm.value).subscribe(
      (response: any)=>{
        console.log(response);
        console.log(response.role);
        this.userAuthService.setRoles(response.role);
        this.router.navigate(['/login']);
        const role = response.role;
        alert("Bạn đã tạo tài khoản thành công")
      },
      (Error) => {
        alert("Account already exists");
      }
    );
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>Document</title>
</head>
<body>
  <div class="box-form">
    <div class="left">
      <div class="overlay">
      <h3>SoftDreams</h3>
      <h2>Company</h2>
      <h5>Nâng tầm quản trị doanh nghiệp</h5>
      <p class="p-login"><i class="fa-solid fa-phone" style="color: #c2c2c2;"></i> Hotline: 1900 33 69</p>
      <p><i class="fa-solid fa-location-dot" style="color: #bdbcbc;"></i> Nhà khách ATS, số 8 Phạm Hùng, Mễ Trì, Nam Từ Liêm, Hà Nội</p>
      </div>
    </div>
    <div class="right">
      <h4 class="text-sign-up">Sign-up</h4>
      <form (ngSubmit) = "onSubmit()">
        <div class="form-group">
          <label>Full Name</label>
          <input type="text" class = "form-control" id = "username"
            [(ngModel)] = "employee.username" name = "username">
        </div>

        <div class="form-group">
          <label>Address</label>
          <input type="text" class = "form-control" id = "address"
            [(ngModel)] = "employee.address" name = "address">
        </div>

        <div class="form-group">
          <label>Email</label>
          <input type="text" class = "form-control" id = "email"
            [(ngModel)] = "employee.email" name = "email">
        </div>

        <div class="form-group">
          <label>Phone Number</label>
          <input type="text" class = "form-control" id = "phoneNumber"
            [(ngModel)] = "employee.phoneNumber" name = "phoneNumber">
        </div>
        <div class="form-group">
          <label>Position</label>
          <input type="text" class = "form-control position" id = "position"
            [(ngModel)] = "employee.position" name = "position">
        </div>

        <button class = "btn btn-success" type="submit">Submit</button>
      </form>
    </div>
  </div>
</body>
</html>
